import styled from 'styled-components';

export const Container = styled.div`
    background: #1D1726;
    width: 100%;
    height: 100%;
`;

export const TopBar = styled.div`
    background: #1D1726;
    padding: 20px;

`;

export const List = styled.ul`
    list-style: none;
    display: flex;

    > li {
        border: 1px solid #B820FF;
        box-shadow: 10px 5px 5px #B820FF 0.1;
        border-radius: 5px;
        background: #B820FF;
        font-size: 30px;

        padding: 5px;
        margin-right: 10px;

        &:hover {
            border: 1px solid #B820FF;
        background: #B820FF;
        }
    }
`;

export const Wrapper = styled.div`

`;
