import styled from 'styled-components';

export const TopBar = styled.div`
    background: #4E3E65;
    padding: 20px;
`;

export const List = styled.ul`
    list-style: none;
    display: flex;

    > li {
        border: 1px solid #078BD3;
        border-radius: 5px;
        background: #078BD3;
        font-size: 30px;

        padding: 5px;
        margin-right: 10px;

        &:hover {
            border: 1px solid #0EBDF2;
        background: #0EBDF2;
        }
    }
`;

export const Content = styled.div``;
