import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

import { doGet } from '../../helpers/ApiHelpers';

import { Container } from './styles';

const Detail = () => {
    const [person, setPerson] =  useState(null);
    const { id } = useParams()

    useEffect(() => {
        doGet(`people/${id}`).then(setPerson);
    }, [id]);

    if(!person) {
        return (<Container><h1>Loading...</h1></Container>)
    }

    return <Container>
                <h1>{person.name}</h1>
                <h3>
                    {person.name} is {person.height}cm tall and his mass is {person.mass}kg. Their hair is {person.hair_color} and 
                    their eyes are {person.eye_color}. They were born in {person.birth_year}.
                </h3>
            </Container>
};

export default Detail;
