import styled from 'styled-components';

export const TopBar = styled.div`
    background: #3B707C;
    padding: 20px;
`;

export const List = styled.ul`
    list-style: none;
    display: flex;

    > li {
        border: 1px solid;
        border-radius: 5px;
        background: #078BD3;
        font-size: 30px;

        padding: 5px;
        margin-right: 10px;

        &:hover {
        background: #0EBDF2;
        }
    }
`;
